import Header from "../../header/Header";
import "./Allassociations.scss";
import { useState } from "react";
import Footer from "../../footer/Footer";
import RestaurantsGrid from "./AssociationsGrid";
import React from "react";
import { Link } from "react-router-dom";

const AllRestaurants: React.FC = () => {
  const [filter, setFilter] = useState("all");
  return (
    <div className="all-restaurants-page">
      <Header />
      <br /> <br />
      <h2>Restaurants</h2>
      <div className="categories-div">
        <Link to="/associations/all/1" onClick={() => setFilter("all")} className={`${filter === "all" ? 'all':'filterLink'}`}>All</Link>
        <Link to="/associations/new/1" onClick={() => setFilter("new")} className={`${filter === "new" ? 'new':'filterLink'}`}>New</Link>
        <Link to="/associations/popular/1" onClick={() => setFilter("popular")} className={`${filter === "popular" ? 'popular':'filterLink'}`}>Most Popular</Link>
        <Link to="/associations/open/1" onClick={() => setFilter("open")} className={`${filter === "open" ? 'open':'filterLink'}`}>Open Now</Link>
      </div>
      <br /> 
      <RestaurantsGrid filter={filter} />
      <hr />
      <Footer />
    </div>
  );
};

export default AllRestaurants;
